import "./App.css";
import React, { useEffect, useState } from "react";
import Message from "./components/Message";
import Input from "./components/Input";


function randomName() {
  const names = [
    "George",
    "Rosa",
    "Alexander",
    "Amelia",
    "Winston",
    "Cleopatra",
    "Abraham",
    "Marilyn",
    "Nelson",
    "Leonardo",
    "Joan",
    "Albert",
    "Marie",
    "Martin",
    "Elizabeth",
    "Thomas",
    "Florence",
    "William",
    "Mahatma",
    "Genghis",
  ];

  const colors = ["Lion", "Wolf", "Shark", "Penguin", "Gorilla", "Koala", "Owl", "Dolphin", "Zebra", "Panda", "Kangaroo", "Squirrel", "Elephant", "Eagle", "Crocodile", "Peacock", "Lemur", "Flamingo", "Lobster", "Bison", "Toucan", "Chimpanzee", "Otter", "Jellyfish",
    "Meerkat", "Sloth", "Corgi", "Raccoon"];

  const name = names[Math.floor(Math.random() * names.length)];
  const color = colors[Math.floor(Math.random() * colors.length)];
  return name + color;
}



const App = () => {
  const [member, setMember] = useState({
    username: randomName(),
  });

  const [drone, setDrone] = useState();
  const [messages, setMessages] = useState([]);


  useEffect(() => {
    if (!drone) {
      const drone = new window.Scaledrone("UeDuN68LJIbS29SF", {
        data: member,
      });
      drone.on("open", (error) => {
        if (error) {
          return console.error(error);
        }
        member.id = drone.clientId;
        setMember(member);
      });
      setDrone(drone);
    } else {
      const room = drone.subscribe("observable-room");
      room.on("message", (message) => {
        const { member, data } = message;
        setMessages((mess) => [...mess, { member, textMessage: data }]);
      });
    }
  }, [drone, member]);

  const onSendMessage = (message) => {
    drone.publish({
      room: "observable-room",
      message,
    });
  };

  return (
    <div className="chatContainer">
      <Message messages={messages} currentMember={member} />
      <Input onSendMessage={onSendMessage} />
    </div>
  );
};
export default App;
