import React from 'react';

function Message({ member, data, id, me }) {
  const { username, color } = member.clientData;
  const messageFromMe = member.id === me.id;
  const className = messageFromMe ? 'currentMember' : '';

  return (
    <li key={id} className={`Messages-message ${className}`}>
      <span
        className="avatar"
        style={{ backgroundColor: color }}
      />
      <div className="Message-content">
        <div className="username">
          {username}
        </div>
        <div className="text">{data}</div>
      </div>
    </li>
  );
}

export default function Messages({ messages, me }) {
  return (
    <ul className="Messages-list">
      {messages.map((m) => (
        <Message key={m.id} member={m.member} data={m.data} id={m.id} me={me} />
      ))}
    </ul>
  );
}
