import React, { Component } from "react";
import UserInput from "./components/UserInput";
import Messages from "./Messages";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      messages: [],
    };

    this.drone = new window.Scaledrone("UeDuN68LJIbS29SF", {
      data: this.state.chatmember, // Ovdje provjerite kako želite postaviti podatke
    });
  }

  handleSendMessage = (message) => {
    this.setState((prevState) => ({
      messages: [...prevState.messages, message],
    }));
  };

  render() {
    return (
      <div className="App">
        <UserInput onSendMessage={this.handleSendMessage} />
        <div className="MessageList">
          {this.state.messages.map((message, index) => (
            <div key={index}>{message}</div>
          ))}
        </div>

        {/* Render the Messages component here */}
        <Messages messages={this.state.messages} currentMember={this.state.currentMember} />
      </div>
    );
  }
}

export default App;
