import React, { Component } from "react";
import UserInput from "./components/UserInput";
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      messages: [], // You should have a state to store messages in your App component
    };
  }

  // Function to handle message submission
  handleSendMessage = (message) => {
    // Add the new message to your messages state
    this.setState((prevState) => ({
      messages: [...prevState.messages, message],
    }));
  };

  render() {
    return (
      <div className="App">
        {/* Render the UserInput component and pass handleSendMessage as a prop */}
        <UserInput onSendMessage={this.handleSendMessage} />
        {/* Render your messages here */}
        <div className="MessageList">
          {this.state.messages.map((message, index) => (
            <div key={index}>{message}</div>
          ))}
        </div>
      </div>
    );
  }
}

export default App;
